from collections import defaultdict

class Solution:
    def recoverArray(self, n: int, sums: List[int]) -> List[int]:
        sums = sorted(sums)
        res = []
        while len(sums) > 1:
            diff = sums[-1] - sums[-2]
            freq = defaultdict(int)
            for num in sums:
                freq[num] += 1
            excluding = []
            including = []
            found_zero = False
            for num in sums:
                if freq[num] > 0:
                    if num == 0:
                        found_zero = True
                    if freq[num + diff] > 0:
                        if found_zero:
                            including.append(num)
                        else:
                            excluding.append(num)
                        freq[num] -= 1
                        freq[num + diff] -= 1
            if found_zero:
                sums = including
                res.append(diff)
            else:
                sums = excluding
                res.append(-diff)
        return res
