class Solution:
    def earliestAndLatest(self, n: int, firstPlayer: int, secondPlayer: int) -> List[int]:
        from functools import lru_cache

        firstPlayer -= 1
        secondPlayer -= 1

        @lru_cache(maxsize=None)
        def dp(mask, round):
            players = []
            for i in range(n):
                if mask & (1 << i):
                    players.append(i)
            if len(players) < 2:
                return (float('inf'), float('-inf'))

            next_players = []
            left = 0
            right = len(players) - 1
            while left < right:
                a = players[left]
                b = players[right]
                if (a == firstPlayer and b == secondPlayer) or (a == secondPlayer and b == firstPlayer):
                    return (round, round)
                if a == firstPlayer or a == secondPlayer:
                    next_players.append(a)
                elif b == firstPlayer or b == secondPlayer:
                    next_players.append(b)
                else:
                    pass
                left += 1
                right -= 1
            if left == right:
                next_players.append(players[left])

            from itertools import product

            choices = []
            left = 0
            right = len(players) - 1
            while left < right:
                a = players[left]
                b = players[right]
                if (a == firstPlayer and b == secondPlayer) or (a == secondPlayer and b == firstPlayer):
                    return (round, round)
                if a == firstPlayer or a == secondPlayer:
                    choices.append([a])
                elif b == firstPlayer or b == secondPlayer:
                    choices.append([b])
                else:
                    choices.append([a, b])
                left += 1
                right -= 1
            if left == right:
                choices.append([players[left]])

            min_round = float('inf')
            max_round = float('-inf')
            for selected in product(*choices):
                next_players = list(selected)
                next_players.sort()
                next_mask = 0
                for p in next_players:
                    next_mask |= (1 << p)
                if next_mask != mask:
                    res = dp(next_mask, round + 1)
                    min_round = min(min_round, res[0])
                    max_round = max(max_round, res[1])

            return (min_round, max_round)

        initial_mask = (1 << n) - 1
        result = dp(initial_mask, 1)
        return [result[0], result[1]] if result[0] != float('inf') else [0, 0]
