import math
from typing import List

class Solution:
    def minSkips(self, dist: List[int], speed: int, hoursBefore: int) -> int:
        n = len(dist)
        INF = float('inf')
        dp = [[INF] * (n + 1) for _ in range(n + 1)]
        dp[0][0] = 0.0

        for i in range(1, n + 1):
            d = dist[i - 1]
            t = d / speed
            for j in range(i + 1):
                if j > 0:
                    dp[i][j] = dp[i - 1][j - 1] + t
                if j < i:
                    ceil_time = math.ceil(dp[i - 1][j] + 1e-9)
                    dp[i][j] = min(dp[i][j], ceil_time + t)

        for skips in range(n + 1):
            if dp[n][skips] <= hoursBefore + 1e-9:
                return skips
        return -1
