class Solution:
    def connectTwoGroups(self, cost: List[List[int]]) -> int:
        size1, size2 = len(cost), len(cost[0])
        min_cost2 = [min(cost[i][j] for i in range(size1)) for j in range(size2)]

        dp = [float('inf')] * (1 << size2)
        dp[0] = 0

        for i in range(size1):
            new_dp = [float('inf')] * (1 << size2)
            for mask in range(1 << size2):
                if dp[mask] == float('inf'):
                    continue
                for j in range(size2):
                    new_mask = mask | (1 << j)
                    if dp[mask] + cost[i][j] < new_dp[new_mask]:
                        new_dp[new_mask] = dp[mask] + cost[i][j]
            dp = new_dp

        min_total = float('inf')
        for mask in range(1 << size2):
            total = dp[mask]
            for j in range(size2):
                if not (mask & (1 << j)):
                    total += min_cost2[j]
            if total < min_total:
                min_total = total

        return min_total
