class Solution:
    def minCost(self, houses: List[int], cost: List[List[int]], m: int, n: int, target: int) -> int:
        dp = [[[float('inf')] * (target + 1) for _ in range(n + 1)] for __ in range(m)]

        if houses[0] == 0:
            for color in range(1, n + 1):
                dp[0][color][1] = cost[0][color - 1]
        else:
            dp[0][houses[0]][1] = 0

        for i in range(1, m):
            for color in range(1, n + 1):
                for neighborhoods in range(1, target + 1):
                    if houses[i] != 0 and houses[i] != color:
                        continue  # Skip if already painted and not matching current color

                    current_cost = 0
                    if houses[i] == 0:
                        current_cost = cost[i][color - 1]

                    min_cost = dp[i - 1][color][neighborhoods]

                    for prev_color in range(1, n + 1):
                        if prev_color != color:
                            if neighborhoods > 1 and dp[i - 1][prev_color][neighborhoods - 1] < min_cost:
                                min_cost = dp[i - 1][prev_color][neighborhoods - 1]

                    if min_cost != float('inf'):
                        dp[i][color][neighborhoods] = min_cost + current_cost

        result = min(dp[m - 1][color][target] for color in range(1, n + 1))
        return result if result != float('inf') else -1
